package com.yoursway.osgi.javaflow;import org.apache.commons.javaflow.bytecode.transformation.bcel.BcelClassTransformer;import org.eclipse.osgi.baseadaptor.HookConfigurator;import org.eclipse.osgi.baseadaptor.HookRegistry;public class InstrumentationHookConfigurator implements HookConfigurator {	public void addHooks(HookRegistry hookRegistry) {		InstrumentationHook hook = new InstrumentationHook(new BcelClassTransformer());		hookRegistry.addAdaptorHook(hook);		hookRegistry.addClassLoadingHook(hook);	}}